package nct

import (
	"dna"
)

func ExampleDecryptLRC() {
	var data dna.String = ""

	lrc, err := DecryptLRC(data)
	if err == nil {
		if lrc.Match(`Không gian quanh đây`) == true && lrc.Match(`rồi cũng sẽ quen dần`) == true {
			dna.Log("Success")
		} else {
			dna.Log("Failed")
		}
	}
	// Output:
	// Success
}
